import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ImageUpload from './components/ImageUpload';
import ProcessedImage from './components/ProcessedImage';
import OperationsList from './components/OperationsList';
import './App.css';

function App() {
  // Estado para armazenar a imagem enviada e as operações realizadas
  const [uploadedImage, setUploadedImage] = useState(null);
  const [appliedOperations, setAppliedOperations] = useState([]);

  // Função para lidar com o upload da imagem
  const handleImageUpload = (data) => {
    setUploadedImage(data); // Define a imagem enviada
    setAppliedOperations([]); // Limpa as operações aplicadas
  };

  // Função para lidar com as operações aplicadas na imagem
  const handleOperationApply = (operations) => {
    setAppliedOperations(operations); // Define as operações aplicadas
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Rota para a página de upload e operações */}
          <Route path="/" element={<ImageUpload onImageUpload={handleImageUpload} />} />

          {/* Componente ProcessedImage é renderizado se houver uma imagem enviada */}
          {uploadedImage && (
            <Route
              path="/processed"
              element={
                <ProcessedImage
                  uploadedImage={uploadedImage}
                  appliedOperations={appliedOperations}
                  onOperationApply={handleOperationApply}
                />
              }
            />
          )}

          {/* Componente OperationsList é renderizado se houver uma imagem enviada */}
          {uploadedImage && (
            <Route
              path="/operations"
              element={<OperationsList onOperationApply={handleOperationApply} />}
            />
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
